package com.wonder.xmppProjectSample;import org.junit.jupiter.api.Test;import reactor.core.publisher.Flux;import reactor.core.publisher.Mono;public class ReactiveProgrammingWorkFlowTest {    @Test    void monoTest(){        Mono<?> just = Mono.just(1)                .then(Mono.error(new RuntimeException("test "))).log();        just.subscribe(System.out::println);    }    @Test    void fluxTest(){        Flux<?> flux = Flux.just(1,2,3,4,5)                .concatWith(Flux.error(new RuntimeException(" test flux")))                .log();        flux.subscribe(System.out::println);    }}